import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [tasks, setTasks] = useState([]);
  const [name, setName] = useState('');
  const [editId, setEditId] = useState(null);
  const [editName, setEditName] = useState('');

  const API_URL = "http://localhost:5000";

  useEffect(() => { fetchTasks(); }, []);

  const fetchTasks = async () => {
    const res = await axios.get(`${API_URL}/tasks`);
    setTasks(res.data);
  };

  const addTask = async () => {
    if (!name.trim()) return;
    await axios.post(`${API_URL}/tasks`, { name });
    setName('');
    fetchTasks();
  };

  const deleteTask = async (id) => {
    await axios.delete(`${API_URL}/tasks/${id}`);
    fetchTasks();
  };

  const updateTask = async () => {
    if (!editName.trim()) return;
    await axios.put(`${API_URL}/tasks/${editId}`, { name: editName });
    setEditId(null);
    setEditName('');
    fetchTasks();
  };

  return (
    <div style={{ 
      maxWidth: "600px",
      margin: "40px auto",
      padding: "20px",
      fontFamily: "Arial",
      border: "1px solid #ddd",
      borderRadius: "12px",
      boxShadow: "0 3px 10px rgba(0,0,0,0.1)"
    }}>

      <h2 style={{ textAlign: "center" }}>MERN CRUD App</h2>

      {/* ADD FORM */}
      <div style={{ display: "flex", gap: "10px", marginBottom: "20px" }}>
        <input 
          value={name}
          onChange={e => setName(e.target.value)}
          placeholder="Enter task"
          style={{
            flex: 1,
            padding: "10px",
            borderRadius: "6px",
            border: "1px solid #aaa"
          }}
        />
        <button 
          onClick={addTask}
          style={{
            padding: "10px 20px",
            background: "green",
            color: "white",
            border: "none",
            borderRadius: "6px",
            cursor: "pointer"
          }}
        >
          Add
        </button>
      </div>

      {/* TASK LIST */}
      <ul style={{ listStyle: "none", padding: 0 }}>
        {tasks.map(task => (
          <li 
            key={task._id}
            style={{
              padding: "12px",
              marginBottom: "10px",
              border: "1px solid #ddd",
              borderRadius: "8px",
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center"
            }}
          >
            {editId === task._id ? (
              <input
                value={editName}
                onChange={(e) => setEditName(e.target.value)}
                style={{
                  flex: 1,
                  marginRight: "10px",
                  padding: "8px",
                  borderRadius: "6px",
                  border: "1px solid #aaa"
                }}
              />
            ) : (
              <span>{task.name}</span>
            )}

            <div style={{ display: "flex", gap: "10px" }}>
              {editId === task._id ? (
                <button
                  onClick={updateTask}
                  style={{
                    padding: "6px 14px",
                    background: "blue",
                    color: "white",
                    border: "none",
                    borderRadius: "6px",
                    cursor: "pointer"
                  }}
                >
                  Save
                </button>
              ) : (
                <button
                  onClick={() => {
                    setEditId(task._id);
                    setEditName(task.name);
                  }}
                  style={{
                    padding: "6px 14px",
                    background: "orange",
                    color: "white",
                    border: "none",
                    borderRadius: "6px",
                    cursor: "pointer"
                  }}
                >
                  Edit
                </button>
              )}

              <button
                onClick={() => deleteTask(task._id)}
                style={{
                  padding: "6px 14px",
                  background: "red",
                  color: "white",
                  border: "none",
                  borderRadius: "6px",
                  cursor: "pointer"
                }}
              >
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
export default App;
